// import logo from './logo.svg';
import './App.css';
import * as React from 'react';
import Palette from './Palette.js';
import { Button, TextField } from '@mui/material';

function App() {

  const [colors, setColors] = React.useState(['#D57B4F','#C1D54F','#4FD554','#4FD0D5','#4F65D5', '#D54F8C']);
  const [num, setNum] = React.useState(5);
  const [paletteName, setPaletteName] = React.useState("Sunny Day on the beach");

  const changeColors = () => {
    setColors(['#D5754F', '#5c97f5','#4FD654','#4FD0D5','#9caf67', '#be411e']);
    let c = [];
    for (let ii = 0; ii < num; ii++) {
      c.push('#'+Math.floor(Math.random()*16777215).toString(16));
    }
    setColors(c);
    console.log("Change colors");
  }

  const changeNumOfColors = (event) => {
    setNum(event.target.value);
  }

  return (
    <div className="App">
      <div style={{padding: 50, backgroundColor: 'black'}}>
        <div className="App-header">
          Color Palette Generator
        </div>
        <TextField
            id="outlined-number"
            label="Number of Colors"
            type="number"
            InputLabelProps={{
              shrink: true,
            }}
            defaultValue={num}
            onChange={changeNumOfColors}
            style={{marginRight: 15,}}
          />
        <Button className="Generate-button" variant="contained" onClick={changeColors}>
          Generate New Color Palette
        </Button>
      </div>
      <div className="Palette-name">{paletteName}</div>  
      <Palette className="Palette" colors={colors}/>
    </div>
  );
}

export default App;
