import './App.css';
import * as React from 'react';
import Palette from './Palette.js';
import { Button, TextField } from '@mui/material';
import tracery from 'tracery-grammar';
import { GetColorName } from 'hex-color-to-color-name';

function App() {

  const [colors, setColors] = React.useState(['#D57B4F','#C1D54F','#4FD554','#4FD0D5','#4F65D5', '#D54F8C']);
  const [num, setNum] = React.useState(5);
  const [paletteName, setPaletteName] = React.useState("Sunny Day on the beach");

  const changeColors = () => {
    let c = [];
    for (let ii = 0; ii < num; ii++) {
      c.push('#' + (Math.random() * 0xFFFFFF << 0).toString(16).padStart(6, '0'));
    }
    setColors(c);
  }

  React.useEffect(() => {
    setPaletteName(grammar.flatten('#origin#'));
    // const colorName = GetColorName(colors[0])
    // setPaletteName(colorName); 
  }, [colors]);

  const changeNumOfColors = (event) => {
    setNum(event.target.value);
  }

  var grammar = tracery.createGrammar({
    "origin": ["#redAdj.capitalize# #blueNoun#", "#redAdj.capitalize# #yellowNoun#","#redAdj.capitalize# #greenNoun#","#redAdj.capitalize# #orangeNoun#","#redAdj.capitalize# #purpleNoun#","#redAdj.capitalize# #pinkNoun#","#redAdj.capitalize# #brownNoun#","#redAdj.capitalize# #greyNoun#","#redAdj.capitalize# #whiteNoun#","#redAdj.capitalize# #blackNoun#"],
