    "blueAdj": ["trusting","sad","peaceful","calm","harmonious","loyal","sky","successful","blueberry","swimming","peacock"],
    "yellowAdj":["sunny","happily","lemon","joyful","joyous","hopeful","curiously","young","fun","banana","buttercup","taxi","sandy","starry"],
    "greenAdj": ["grassy","healthy","wealthy","envious","natural","young","lucky","harmonious","safe","hopeful","lime","cucumber","shamrock","pea","kiwi","pickle"],
    "orangeAdj": ["fun","excited","warm","creative","energetic","vibrant","optimistic","enthusiastic","uplifting","spontaneous","orange","pumpkin"],
    "purpleAdj": ["royal","magical","wise","mystical","spiritual","fantastical","mysterious","grape","eggplant","lavender","fig","plum","jelly","petunia"],
    "pinkAdj": ["lovely","romantic","summer","calm","soft","feminine","flamingo","cotton candy","rhubarb","cupcake","ballerina","lotus","cherry blossom","raspberry","guava","coral"],
    "brownAdj": ["earthly","stable","homey","comfortable","outdoor","reliable","vintage","chocolate","mushroom","bear","pinecone","leather","moose","wood","coffee","acorn","gingerbread","fudge","cabin"],
    "greyAdj": ["security","intelligent","dignity","elderly","sad","boring","control","moon","mice","wolf","rock"],
    "whiteAdj": ["purity","birth","clean","winter","snow","good","innocent","cold","cloud","polar bear","goose","pearl","vanilla","glacier","bone","daisy","jasmine","coconut","swan","sheep"],
    "blackAdj": ["power","formal","elegant","crow","raven","fear","mystery","evil","protection","elegance","death","bat","spider","obsidian","charcoal","coal","blackberry","gorilla"],
    "redNoun": ["warmth","fire","love","power","confidence","passion","strength","blood","danger","heart","adventure","revenge","cherry","anger","heat","Mars","speed","strawberry","tomato","lobster","rose","fire hydrant","lady bug"],
    "blueNoun": ["trust","sadness","water","peace","harmony","loyalty","sky","success","blueberry","swimming pool","Neptune","Smurf","peacock","denim"],
    "yellowNoun":["sun","sunshine","happiness","lemon","lemonade","bees","honey","joy","hope","curiosity","intellect","youth","fun","banana","rubber duck","big bird","pac-man","buttercup","taxi","sand","beach","corn","pineapple","school","school bus","star"],
    "greenNoun": ["Spring","grass","health","wealth","envy","tree","nature","youth","luck","harmonious","safe","growth","hope","lime","cucumber","shamrock","pea","kermit","frog","broccoli","tennis","kiwi","pickle","leaves"],
    "orangeNoun": ["fun","excitement","warm","autumn","youth","creativity","energy","vibrant","optimistic","enthusiastic","uplifting","spontaneous","orange","pumpkin","Cheetos","Garfield","basketball","fanta","carrot","tiger","Nemo"],
    "purpleNoun": ["royal","royalty","magic","wisdom","mystical","spiritual","justice","fantasy","mysterious","grape","eggplant","jellyfish","Barney","lavender","fig","plum","jelly","petunia"],
    "pinkNoun": ["love","romance","summer","calm","romance","soft","feminine","flamingo","cotton candy","rhubarb","cupcake","flower","ballerina","lotus","cherry blossom","raspberry","guava","coral"],
    "brownNoun": ["earth","stable","home","comfort","outdoors","reliable","vintage","chocolate","mushroom","bear","pinecone","leather","moose","wood","coffee","acorn","gingerbread","fudge","cabin"],
    "greyNoun": ["security","intelligent","dignity","elderly","sad","boring","control","moon","mice","wolf","rock"],
    "whiteNoun": ["purity","birth","clean","winter","snow","good","innocent","cold","cloud","polar bear","goose","pearl","vanilla","glacier","bone","daisy","jasmine","coconut","swan","sheep"],
    "blackNoun": ["power","formal","crow","raven","fear","mystery","evil","protection","elegance","death","bat","spider","obsidian","charcoal","coal","blackberry","gorilla"]
  });
  grammar.addModifiers(tracery.baseEngModifiers); 


  return (
    <div className="App">
      <div style={{padding: 30, backgroundColor: '#d6d6d6'}}>
        <div className="App-header">
          Color Palette Generator
        </div>
        <TextField
            id="outlined-number"
            label="Number of Colors"
            type="number"
            InputLabelProps={{
              shrink: true,
            }}
            defaultValue={num}
            onChange={changeNumOfColors}
            style={{marginRight: 15,}}
          />
        <span style={{paddingTop: 30}}>
          <Button className="Generate-button" variant="contained" onClick={changeColors}>
            Generate New Color Palette
          </Button>
        </span>    
      </div>
      <div className="Palette-name">{paletteName}</div>  
      <Palette className="Palette" colors={colors}/>
      <div className='Footer'>
            Created by Katherine Tuohy
      </div>
    </div>
  );
}

export default App;
