// import logo from './logo.svg';
import './App.css';
import * as React from 'react';
import Palette from './Palette.js';
import { Button, TextField } from '@mui/material';
import tracery from 'tracery-grammar';
import { GetColorName } from 'hex-color-to-color-name';
import redAdj from './tracery/redAdj.json';

function App() {

  const [colors, setColors] = React.useState(['#D57B4F','#C1D54F','#4FD554','#4FD0D5','#4F65D5', '#D54F8C']);
  const [colorsTracery, setColorsTracery] = React.useState(tracery.createGrammar(redAdj))
  const [num, setNum] = React.useState(5);
  const [paletteName, setPaletteName] = React.useState("Sunny Day on the beach");

  const changeColors = () => {
    let c = [];
    for (let ii = 0; ii < num; ii++) {
      c.push('#' + (Math.random() * 0xFFFFFF << 0).toString(16).padStart(6, '0'));
    }
    setColors(c);
  }

  React.useEffect(() => {
    var redA = colorsTracery.flatten('#redAdj#');
    var blueNoun = colorsTracery.flatten('#blueNoun#')
    setPaletteName(redA + ' ' + blueNoun);
    // const colorName = GetColorName(colors[0])
    // setPaletteName(colorName); 
  }, [colors]);

  const changeNumOfColors = (event) => {
    setNum(event.target.value);
  }

  var grammar = tracery.createGrammar(redAdj);
  grammar.addModifiers(tracery.baseEngModifiers); 


  return (
    <div className="App">
      <div style={{padding: 30, backgroundColor: '#d6d6d6'}}>
        <div className="App-header">
          Color Palette Generator
        </div>
        <TextField
            id="outlined-number"
            label="Number of Colors"
            type="number"
            InputLabelProps={{
              shrink: true,
            }}
            defaultValue={num}
            onChange={changeNumOfColors}
            style={{marginRight: 15,}}
          />
        <span style={{paddingTop: 30}}>
          <Button className="Generate-button" variant="contained" onClick={changeColors}>
            Generate New Color Palette
          </Button>
        </span>    
      </div>
      <div className="Palette-name">{paletteName}</div>  
      <Palette className="Palette" colors={colors}/>
      <div className='Footer'>
            Created by Katherine Tuohy
      </div>
    </div>
  );
}

export default App;
