// import logo from './logo.svg';
import './App.css';
import * as React from 'react';
import Palette from './Palette.js';
import { Button, TextField } from '@mui/material';

function App() {

  const [colors, setColors] = React.useState(['#D57B4F','#C1D54F','#4FD554','#4FD0D5','#4F65D5', '#D54F8C']);
  const [num, setNum] = React.useState(5);
  const [paletteName, setPaletteName] = React.useState("Sunny Day on the beach");

  const changeColors = () => {
    let c = [];
    for (let ii = 0; ii < num; ii++) {
      c.push('#'+Math.floor(Math.random()*16777215).toString(16));
    }
    setColors(c);
    console.log("Change colors");
  }

  const changeNumOfColors = (event) => {
    setNum(event.target.value);
  }

  const changePaletteName = () => {
    grammar.addModifiers(tracery.baseEngModifiers); 
  }

  var grammar = tracery.createGrammar({
    'red': ['warm','fire','love','power','confidence','passion','strength','blood','danger','heart','adventure','revenge','cherry','anger','hot','Mars','speed','strawberry','tomato','lobster','rose','fire hydrant','lady bug'],
    'blue': ['trust','sad','water','peace','calm','harmony','loyalty','sky','success','blueberry','swimming','Neptune','Smurf','peacock','denim'],
    'yellow':['sun','sunshine','happy','lemon','lemonade','bees','honey','joy','hope','curiousity','intellect','young','fun','banana','rubber duck','big bird','pacman','buttercup','taxi','sand','beach','corn','pineapple','school','school bus','star'],
    'green': ['spring','grass','health','wealth','envy','tree','nature','youth','luck','harmoneaous','safe','growth','hope','lime','cucumber','shamrock','pea','kermit','frog','broccoli','tennis','kiwi','pickle','leaves'],
    'orange': ['fun','excitement','warm','autumn','youth','creativity','energy','vibrant','optimistic','enthusiastic','uplifting','spontaneous','orange','pumpkin','cheetos','Garfield','basketball','fanta','carrot','tiger','Nemo'],
    'purple': ['royal','royalty','magic','wisdom','mystical','spiritual','justice','fantasy','mystereous','grape','eggplant','jellyfish','Barney','lavender','fig','plum','jelly','petunia'],
    'pink': ['love','romance','summer','calm','romance','soft','femine','flamingo','cotton candy','rhubarb','cupcake','flower','ballerina','lotus','cherry blossom','raspberry','guava','coral'],
    'brown': ['earth','stable','home','comfort','outdoors','reliable','vintage','chocolate','mushroom','bear','pinecone','leather','moose','wood','coffee','acorn','gingerbread','fudge','cabin'],
    'grey': ['security','inteligent','dignity','elderly','sad','boring','control','moon','mice','wolf','rock'],
    'white': ['purity','birth','clean','winter','snow','good','innocent','cold','cloud','polar bear','goose','pearl','vanilla','glacier','bone','daisy','jasmine','coconut','swan','sheep'],
    'black': ['power','formal','elegant','crow','raven','fear','mystery','evil','protection','elegance','death','bat','spider','obsidian','charcoal','coal','blackberry','gorilla']
  });
  grammar.addModifiers(tracery.baseEngModifiers); 

  return (
    <div className="App">
      <div style={{padding: 50, backgroundColor: '#d6d6d6'}}>
        <div className="App-header">
          Color Palette Generator
        </div>
        <TextField
            id="outlined-number"
            label="Number of Colors"
            type="number"
            InputLabelProps={{
              shrink: true,
            }}
            defaultValue={num}
            onChange={changeNumOfColors}
            style={{marginRight: 15,}}
          />
        <Button className="Generate-button" variant="contained" onClick={changeColors}>
          {grammar.flatten('#origin#')}
        </Button>
      </div>
      <div className="Palette-name">{paletteName}</div>  
      <Palette className="Palette" colors={colors}/>
    </div>
  );
}

export default App;
